const getStroadCart = () =>{
    let shoppingCart = {};

        //get the shopping cart from local storage
        const storedCart = localStorage.getItem('shopping-cart');
        if(storedCart){
            shoppingCart = JSON.parse(storedCart);
        }
        return shoppingCart;

}
export {
    getStroadCart
}
